INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0230803', 'Songs for Cassavetes', 2001, 'Justin Mitchell', null, null, 'In this documentary from director Justin Mitchell, the world of the U.S. indie-music scene of the 1980s and ''90s is explored, specifically with regard to how it transcended clichÃ©s about where music was heading in that era. Taking its title from renowned independent cinema pioneer John Cassavetes, the black-and-white film takes a peek at several fringe bands of the period, including Sleater-Kinney, the Make-up, and the Hi-Fives, and their various attitudes toward the ever-changing business.', '/5PblnM9t5fcQpi9ae3CboOpb1J0.jpg', null, 0);
