INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0239060', 'Ik ook van jou', 2001, 'Ruud van Hemert', '/8pUv9F83HX4aSSmjqHHV4UMlHcQ.jpg', null, 'While camping in France, Eric and Eddy fall in with two women. Eric talks to Silke, a Ugandan studying medicine in Holland. She tells him about her life and asks about the book he''s writing: it''s the story of his love affair with Reza. He tells her the story and we see it in flashbacks: Reza explodes into his boring life, she''s unpredictable and their relationship becomes intense.', '/rrOORfxHLmONvyZc8V1vw5G0Fs4.jpg', null, 0);
