INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0239395', 'Cats & Dogs', 2001, 'Lawrence Guterman', '/2GoKwjA0K7b3BnsvBXwm68PkaJk.jpg', null, 'When a professor develops a vaccine that eliminates human allergies to dogs, he unwittingly upsets the fragile balance of power between cats and dogs and touches off an epic battle for pet supremacy. The fur flies as the feline faction, led by Mr. Tinkles, squares off against wide-eyed puppy Lou and his canine cohorts.', '/1RLlzNwnrpMMNFt71OVFAnJp6ww.jpg', null, 0);
