INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0239670', 'Mortal Transfer', 2001, 'Jean-Jacques Beineix', '/rD5BOFRxf8BUySxGH0JZbOFuaBU.jpg', null, 'Michel, a psycho-analyst, falls asleep while listening to his patient Olga, a kleptomaniac and a sexual pervert, tell him how she likes her husband beating her. When he wakes up, he finds Olga having been choked to death. He now has to deal with a body, with Olga''s rich husband who thinks she stole money from him, and with all his patients'' insanity that haunts him.', '/3xSsbuZB9sgfrnLpHd2kOQSCwKL.jpg', null, 0);
