INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0242445', 'Exit Wounds', 2001, 'Andrzej Bartkowiak', '/s1ZQV9vuFiXG94UmT5KtJUtDCvW.jpg', null, 'Maverick cop Orin Boyd always brings down the domestic terrorists he tracks, but he ruffles feathers with his unorthodox techniques -- and soon finds himself reassigned to the toughest district in Detroit. When he discovers a group of detectives secretly operating a drug ring, Boyd joins forces with an unlikely ally -- gangster Latrell Walker -- to bring down the rotten cops.', '/ioPpykV342e0zIuqbJxdOwcMZMS.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0242508', 'Harvard Man', 2001, 'James Toback', '/vdoInWTyXACl3yBJd3JYvr495Tm.jpg', null, 'College has always been a time for experimentation, sexual, cultural and otherwise. "Harvard Man" plays out against a background of love, sex, basketball, crime and experimentation. Action and philosophy in young people''s quest to discover their true identity.', '/uQle1hB3mvsOFgKc0oaoUAcyXKK.jpg', null, 0);
