INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0243210', 'On the Nose', 2001, 'David Caffrey', '/5KwVBsj793G27wBp2oHSnDydUom.jpg', null, 'Brendan is a janitor at a university who has a fondness for gambling, especially betting on horse races. However, he doesn''t have much skill at it, and poorly considered bets have drained the bank account he''d set aside for his daughter''s education. Brendan''s wife has made Brendan promise to give up gambling, but when their daughter announces she''s just been accepted to Trinity College in Ireland, Brendan has to come up with the tuition money, and fast. While doing his sweeping at work, Brendan makes a remarkable discovery - the preserved head of an aboriginal tribesman who, under proper conditions, can pick the winners in horse races.', '/qRHHEqfAsUcWfYJloSSjb52HQVG.jpg', null, 0);
