INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0243585', 'Stuart Little 2', 2002, 'Rob Minkoff', '/dZIRC5SwtfScz3DSSsMPqyTjsni.jpg', null, 'Stuart, an adorable white mouse, still lives happily with his adoptive family, the Littles, on the east side of Manhattan''s Central Park. More crazy mouse adventures are in store as Stuart, his human brother, George, and their mischievous cat, Snowbell, set out to rescue a friend.', '/cIZb3d1kRcmywmoYFQKhMBqvoZt.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0243595', 'Kill Me Later', 2001, 'Dana Lustig', null, null, 'Her goldfish dead, her lover exposed as a rat, Shawn Holloway leaves her bank post and goes to the roof intent on suicide. Before she can leap, she''s taken hostage by Charlie Anders, a fleeing bank robber. He and his partners have stolen a million in cash and plan to escape to Venezuela. Shawn agrees to cooperate if Charlie promises to kill her once he''s in the clear. Parts of the plan go awry, so Charlie has time to try to pierce her bleak manner and self pity, and she has time for reflection. As night falls, their interlude ends: they''re each alone, Charlie facing prison as the police close in, and Shawn staring down at a river from atop a high bridge.', '/jWd0LUXsJ2DkgJWbJRnDk3ZZ0G8.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0243609', 'To End All Wars', 2001, 'David L. Cunningham', '/rbkf8Q6jyZQ6SupRhWeUtW7qQNd.jpg', null, 'Based on a real-life story, this drama focuses on a small group of Allied soldiers in Burma who are held captive by the Japanese. Capt. Ernest Gordon (Ciaran McMenamin), Lt. Jim Reardon (Kiefer Sutherland) and Maj. Ian Campbell (Robert Carlyle) are among the military officers kept imprisoned and routinely beaten and deprived of food. While Campbell wants to rebel and attempt an escape, Gordon tries to take a more stoic approach, an attitude that proves to be surprisingly resonant.', '/r7MNAYQFSIJT7LjQxq27U1IHOoS.jpg', null, 0);
