INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0247425', 'In the Bedroom', 2001, 'Todd Field', '/iVUeGcVv53taEokNOfjLvUkrZfA.jpg', null, 'Summertime on the coast of Maine, "In the Bedroom" centers on the inner dynamics of a family in transition. Matt Fowler is a doctor practicing in his native Maine and is married to New York born Ruth Fowler, a music teacher. He is involved in a love affair with a local single mother. As the beauty of Maine''s brief and fleeting summer comes to an end, these characters find themselves in the midst of unimaginable tragedy.', '/3UzsHGayLvoInQrgIKd4u2y9BeA.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0247427', 'In the Shadows', 2001, 'Ric Roman Waugh', '/8sLhFwHphAeXy4f8Wp3APcRuMiL.jpg', null, 'When a stunt man dies in Miami, his uncle, a New York mob boss, sends a hit man to tail the stunt coordinator, whom the boss wants dead. The hit man, Eric O''Bryne, gets close to his mark''s daughter, and through her he asks her dad to teach him to do stunts. While waiting for the word to kill this mentor, Eric discovers the joy of stunt work and of being part of a family. Meanwhile, several people are looking for the dead man''s briefcase of money and a cloth bag of illegal drugs that he stole from an undercover FBI agent now desperate to get the borrowed drugs back to the bureau. The stunt coordinator says that ''we dance in the shadows of death.'' Can Eric step out into the sun?', '/33fyhHi1Vfir1uRJylhzEfvgsSV.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0247638', 'The Princess Diaries', 2001, 'Garry Marshall', '/ncERp0UvXuImUeJcf27kEANcf1r.jpg', null, 'A socially awkward but very bright 15-year-old girl being raised by a single mom discovers that she is the princess of a small European country because of the recent death of her long-absent father, who, unknown to her, was the crown prince of Genovia. She must make a choice between continuing the life of a San Francisco teen or stepping up to the throne.', '/sxpvHk286sXLJWcFrBMNIsXbYsd.jpg', null, 0);
