INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0248190', 'No Such Thing', 2001, 'Hal Hartley', '/tqEUc2nPxNGv80iFEVCP7NdbD8b.jpg', null, 'Tells the story of Beatrice (Sarah Polley), a tabloid journalist whose fiancÃ© is killed by a monster in Iceland. She ends up falling in love with the monster in the end. The monster is immortal, but longs to die. Beatrice helps him achieve this by contacting a scientist who can destroy matter painlessly.', '/4nVzqmH8D0C9RDhXOPl1p9derXz.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0248216', 'Na Tum Jaano Na Hum', 2002, 'Arjun Sablok', '/bLOxNDf3974Dd7JXoY6DM0TE64k.jpg', null, 'Romantic Esha calls up FM Radio''s DJ Bingo to find out the identity of a male who had called during his program. While Bingo is unable to identify him, the male, Rahul Sharma, does call again, and a meeting is arranged. Esha does not show up, but instead decides to correspond with him via a post box numbered address without disclosing her name, to which Rahul agrees, arranges for a numbered postal address himself, and does not disclose his identity as well. Rahul has a friend, Akshaye, who is a womanizer, whose mother asks Rahul to check out a bride for her son. Rahul goes to check this young lady, Esha Malhotra, finds her suitable, and soon marriage preparations are underway. Rahul''s romance for the unknown girl will be shattered after he finds out that she is none other than Esha.', '/hEzVJlKJq4acNAxUh6lhtqflBGT.jpg', null, 0);
