INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0250125', 'Under the Skin of the City', 2001, 'Rakhshan Banietemad', null, null, 'Tuba works daily at a grueling textile factory in Iran, returning home every night to deal with the rest of her problematic family, which includes: a pregnant daughter whose husband beats her regularly; a teenage son, who''s been getting into trouble due to his burgeoning career in radical politics; and an older son who goes to great lengths--such as attempting to sell the family''s meager house--in order to get an engineering job in Japan as a means of getting out of Iran.', '/5vyDTfpiNHnYm8QJIxIGax50VY0.jpg', null, 0);
