INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0250214', 'Anthrax', 2001, 'Rick Stevenson', null, null, 'When cattle mysteriously start dying of anthrax in a small Alberta ranching community, an RCMP officer tries to keep the peace as ranchers lay the blame on a local agricultural research center. A reporter encourages the angry ranchers - led by the Mountie''s mother-in-law - to stage a sit-in at the research center. When an eccentric old rancher dies after being exposed to a deadly strain of anthrax, the protesters are quarantined. No one notices that the reporter has disappeared - along with several vials of the deadly bacteria. The RCMP officer begins his own investigation, gleaning information from a U.S. government agent. He finds himself racing against time to recover the stolen anthrax.', '/kktOrI03FGRxX2OB7IacU8LicEw.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0251636', 'Chico', 2001, 'Ibolya Fekete', '/wrLcRaxmiJmegD7XbRu19j1RuDA.jpg', null, 'The adventures of a young man as he moves from the Latin-American revolutions in the sixties and seventies, through Hungary in the eighties, to the Croatian war in 1991.', '/zhh89V5Bq3CoxyMfKyHbp58FTTF.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0251661', 'One Day in August', 2001, 'Constantine Giannaris', null, null, 'Middle August in Athens. Three families, living in a three storey building, leave for their summer vacations. Everyone is wishing for a miracle to happen this summer. In the meantime, a drifter breaks into their apartments and rummages their personal belongings, thus unfolding hidden secrets.', '/hgkA6dCVPYdqcMZmFWtM00QQR4r.jpg', null, 0);
