INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0253474', 'The Pianist', 2002, 'Roman Polanski', '/xUR95VFV1UTyzS1C8Lj06hi2n2M.jpg', null, 'The true story of pianist Wladyslaw Szpilman''s experiences in Warsaw during the Nazi occupation. When the Jews of the city find themselves forced into a ghetto, Szpilman finds work playing in a cafÃ©; and when his family is deported in 1942, he stays behind, works for a while as a laborer, and eventually goes into hiding in the ruins of the war-torn city.', '/iunmxWkOi7Vk17Ob3G2HwwjgHsr.jpg', null, 0);
