INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0253688', 'Shoot or Be Shot', 2002, 'J. Randall Argue', '/p7ADIvORDYaVn34k3nLXFkfOwuG.jpg', null, 'An escaped mental patient holds a film crew hostage and forces them to film his screenplay.', '/A0qVBAaQl8oNDd3e83mP7tsRtvA.jpg', null, 0);
