INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0270197', 'American Gun', 2002, 'Alan Jacobs', '/hfbKLZmalQG4myA9kjEXlohZ1gx.jpg', null, 'A brilliant story with an unexpected twist. A father uses the serial number on a hand gun in an attempt to track down his daughter''s killer. The truth he uncovers is an unsettling reminder of the dangers of keeping handguns. Stars the legendary James Coburn and the incredibly gifted Virginia Madsen. Written and directed by Alan Jacobs.', '/5LJ8b2XIh2JURI9vJSnxN793LLk.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0270259', 'The Business of Strangers', 2001, 'Patrick Stettner', null, null, 'Julie Styron thinks she is going to be fired, but instead discovers that she is being promoted. Trapped in an airport hotel, she wants to celebrate but finds only the company of her young assistant, Paula Murphy. As the night progresses, the two women get to know each other. They flirt, they drink, they lie; personal flaws are revealed and exposed. But at the end of the night their relationship turns and becomes a complex battle of power, authority, and wit.', '/wLwSnbTxgs7UUvjUoCUZCFbVQ47.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0270270', 'Century Hotel', 2001, 'David Weaver', null, null, 'David Weaver makes his feature debut with this omnibus film in which each tale is told during different points during the 20th century, but in the same hotel room -- room 720. The film opens during the swinging ''20s when a beautiful young woman, married against her will to a brutish thug of a man, endures a tension-fraught honeymoon. During the Depression segment, a mail-order bride from China meets her husband for the first time. Following the end of WWII, a soldier returns home to meet his girlfriend and his best friend. During the paranoia of the 1950s, a professor searches for his wife. During the 1980s, a lawyer has too much sex and debt, and during the dawn of the millennium, a woman comes to a newly refurbished room 720 to meet her Internet lover. Such acclaimed Canadian actors as Tom McCamus, Sandrine Holt, and Colm Feore star in this film, which was screened at the 2001 Toronto Film Festival.', '/zVpPdzUjDdOUDj5wpY9fd4fakYx.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0270291', 'Coral Reef Adventure', 2003, 'Greg MacGillivray', '/pBJ68ki4IVFvX9yDp3DEsIZxlFA.jpg', null, 'Coral Reef Adventure follows the real-life expedition of ocean explorers and underwater filmmakers Howard and Michele Hall. Using large-format cameras, the Halls guide us to the islands and sun-drenched waters of the South Pacific to document the health and beauty of coral reefs. Featuring songs written and recorded by Crosby, Stills &amp; Nash.', '/AfOvFxFKMnPE8rYQ8UOgZvXlI9q.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0270393', 'Groom Lake', 2002, 'William Shatner', '/lCDrQFX5QNZLLGXrSmjHMpK7HlY.jpg', null, 'When Kate discovers she has a terminal illness, she persuades boyfriend Andy to visit Groom Lake, a reputed hotbed of alien activity, hoping to discover proof of life beyond this terrestrial sphere. Soon, the pair crosses paths with desperate government scientists, eccentric locals and a surprising -- and inspiring --conspiracy of epic proportions.', '/wYGZGDyesqGRrg9JzmnJZNeupGX.jpg', null, 0);
