INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0281924', 'I Am Dina', 2002, 'Ole Bornedal', '/vlCehpY1PeqTuvhevTBSxN7HgCv.jpg', null, 'In Northern Norway during the 1860s, a little girl named Dina accidentally causes her mother''s death. Overcome with grief, her father refuses to raise her, leaving her in the care of the household servants. Dina grows up wild and unmanageable, with her only friend being the stable boy, Tomas. She summons her mother''s ghost and develops a strange fascination with death as well as a passion for living. Family friend Jacob encourages Dina''s father to hire Lorch, a tutor who introduces her to the cello. When Dina is old enough, she marries Jacob and moves to Reinsnes, a port he runs with his mother, Karen, and his stepsons Niels and Anders . Niels doesn''t like Dina''s wild ways, or the fact that she has taken over accounting duties at Reinsnes. Dina''s eccentric tendencies become even stronger, eventually leading Jacob into an accident of his own and bringing Tomas back into her life.', '/tlfWqFpX8bFeBCLfd5Im5hwNmJf.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0281954', 'El lado oscuro del corazón 2', 2001, 'Eliseo Subiela', null, null, 'The sequel to the blockbuster Argentinean smash hit. After ten years, Oliverio is still intrigued by the works of great poets and continues to search for true love. When he meets the beautiful... The sequel to the blockbuster Argentinean smash hit. After ten years, Oliverio is still intrigued by the works of great poets and continues to search for true love. When he meets the beautiful Miranda, she suggests that they both move in together and find jobs. Oliviero, however, leaves Miranda in search of his first love Ana who he met ten years prior. Once in Barcelona, he realizes that Ana''s life has drastically changed and their love is in the past. Soon, he meets poet and circus performer, Alejandra. Oliviero becomes smitten and desperately tries bond with her, but she makes things a little more difficult that he ever imagined.', '/aLzYuyY4r4sLt874FbabqPbZPKT.jpg', null, 0);
