INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0282860', 'Princess D', 2002, 'Alan Yuen', '/FgbKRlNZU6XVNKjLYzlwU6iLf0.jpg', null, 'review by LoveHkFilm.com: Joker (Daniel Wu) is a computer programmer working on a 3-D adventure game starring a virtual idol. Stuck for inspiration, he ends up finding it in Ling (Angelica Lee Sum-Kit AKA: Sinjie), a bartender at a dance club. While in a drug-induced stupor, Joker imagines Ling to be the perfect image of his virtual girl. He''s immediately smitten, but more important, he now has a model for his virtual heroine: Princess D.', '/k55Iy3ESbPyTEsKoiVuI1qdsSR6.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0282864', 'Promises', 2001, 'Carlos Bolado', '/hp20DE7UHCr36yiuu0toqX3vjj8.jpg', null, 'Documentarians Justine Shapiro and B.Z. Goldberg traveled to Israel to interview Palestinian and Israeli kids ages 11 to 13, assembling their views on living in a society afflicted with violence, separatism and religious and political extremism. This 2002 Oscar nominee for Best Feature Documentary culminates in an astonishing day in which two Israeli children meet Palestinian youngsters at a refugee camp.', '/sNGY4nb8QwuYUiGt0NpEkhnKk3h.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0282883', 'Quicksand', 2002, 'Sam Firstenberg', null, null, 'Randi Stewart is a sergeant on a marine base where her father is the commanding officer. She is being treated by the new base psychiatrist, Bill Turner. Her brother, Gordon Stewart is running for political office. General Stewart is murdered and Randi is the key suspect. Once Bill starts investigating the crime through his treatment of Randi, he begins to uncover an unusually large amount of suicides on the base. The intrigue grows and so does Bill''s unprofessional feelings towards Randi. Is she the murderer? He doesn''t really want to find out. He just keeps getting sucked deeper and deeper into the "Quicksand".', '/ipCJjHRIQTsQTghK7TLulKtdl4d.jpg', null, 0);
