INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0283387', 'The Holy Land', 2001, 'Eitan Gorlin', '/owNnFDXO4ERzBEG7ap8agTwaIee.jpg', null, 'A coming-of-age saga set against the tense backdrop of Arab-Israeli politics. Mendy is a young Rabbinical student whose mind is too full of curiosity about the world that exists outside the restrictions of Orthodox Judaism. Recognizing his distraction, Mendy''s teacher suggests that he visit a prostitute to get it out of his system. But on a fateful night, when he meets Sacha, a captivating Russian expatriate, who works in a Tel Aviv bordello, his appetite for outside experience only grows stronger.', '/3hINLjqmOup2pgeKRhF4iM9VbBZ.jpg', null, 0);
