INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0297780', 'Beyond the Limits', 2003, 'Olaf Ittenbach', '/yhVLkLjroZWGCyphgxOT3hFv7Lg.jpg', null, 'The rookie journalist Vivian seeks Frederick, the caretaker of the oldest cemetery in an undefined country, asking for an interview and the old man tells the story of two "costumers" and both connected by a mysterious secret. The powerful gangster Jimmie Levinson accuses his former partner Paul Pattuchi of stealing a load of cocaine and orders to kill his beloved girlfriend Clarice. Paul is found dead later in a cottage, and Jimmie and his friends and girlfriends are visited during a dinner party by Robert Downing first and then by the sadistic Mortimer and his partner Rick seeking the truth about Paul and a suitcase with a mysterious relic. The second story takes place in the Middle Ages, where the cruel David Deming and his minion Tom Brewster chase the renegade priest James Flynn in the name of the Inquisition but actually trying to force him to reveal the secret of the Eternal Heart to grant immortality.', '/wIRTb5oM0amk2QlXItJMxTSxfDF.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0297793', 'Black Chicks Talking', 2002, 'Leah Purcell', null, null, 'Performer and writer Leah Purcell talks with five dynamic Indigenous women - Rosanna Angus, Kathryn Hay, Deborah Mailman, Cilla Malone and Tammy Williams - about what it means to be Aboriginal in Australia today. In a series of individual interviews and at one lively dinner party, the women share their experiences and opinions with extraordinary candour. The result is a passionate and challenging exploration of black identity and a celebration of five very different lives.', '/nl1sbgRZi4Tb6IPm3nOg5gZtgiM.jpg', null, 0);
