INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0297905', 'Harmful Insect', 2001, 'Akihiko Shiota', '/d2WatjY0VXDHTixNlhY5BsVGYsu.jpg', null, '13-year-old Sachiko is about to encounter the most difficult moments in her young life. Her father abandons her and her mother attempts to commit suicide by slashing her wrist. Her teacher, whom she relies on heavily, leaves for northern Japan to take another job. Other students start to gossip about Sachiko and her former teacher. Sachiko then drops out of school and attempts to find herself.', '/xObzD7H2K9UoL2QRslxTgHn2vQ1.jpg', null, 0);
