INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0298083', 'No sabe no contesta', 2002, 'Fernando Musa', null, null, 'A handful of young people deal with their personal and emotional tribulations in this teen-centric romantic comedy from Argentina. Laura (Karina Dali) is a woman who has unwittingly stolen the heart of Joaquin (Mariano Martinez), a brash film student, but while he makes plans to woo her, Joaquin has to take care of some other matters first. Joaquin is moving into a new flat, and he and his friends discover just how hard it is to move a piano; meanwhile, he also is drafted into helping his buddy Marcos (Facundo Espinosa) win back his girlfriend, which means lending Marcos his car.', '/6jVaIUuKoKnTBCn8QkSt2QWIOQn.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0298109', 'A Dog Called Pain', 2001, 'Luis Eduardo Aute', null, null, 'Accomplished singer/songwriter and artist turned filmmaker Luis Eduardo Aute combined more than 4,000 of his own drawings with state-of-the-art digital animation techniques for his unique animated movie, A Dog Called Pain. Aute explores the relationships between several eccentric artists and their models in seven black-and-white animated portraits. Aute focuses predominantly, though not exclusively, on great Spanish artists, as he examines the work of Francisco de Goya, Diego Velazquez, Marcel Duchamp, Pablo Picasso, Groucho Marx, and even Woody Allen make brief "cameo" appearances. The film was named for Frieda Kahlo''s own pooch, and dogs figure prominently in the episodes. Through his silent (other than the musical score), somewhat static imagery, Aute focuses not on straightforward storytelling, but on capturing some inner truth about the artists, their relationships with their models, and their historical and cultural environments.', '/iDIPnZNFtep5U5KWR3MmviSFm5m.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0298120', 'Purgatory House', 2004, 'Cindy Baer', null, null, 'An inside glimpse of how it feels to be a teen in today''s disconnected world. PURGATORY HOUSE chronicles the after-life journey of Silver Strand: a lonely teen who abandons her life of turmoil and drug addiction, only to find herself caught somewhere between heaven and hell. There, she must choose whether she will accept her drab existence or discover within herself the power to change.', '/x4w9okSfMyvBvCTe1Hsb3hxVRHu.jpg', null, 0);
