INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0298130', 'The Ring', 2002, 'Gore Verbinski', '/kQOLgJGvFjQ7EwGfaPkW9dlGFXJ.jpg', null, 'It sounded like just another urban legend: A videotape filled with nightmarish images, leading to a phone call foretelling the viewer''s death in exactly seven days. As a newspaper reporter, Rachel Keller was naturally skeptical of the story, until four teenagers all met with mysterious deaths exactly one week after watching just such a tape. Allowing her investigative curiosity to get the better of her, Rachel tracks down the video... and watches it. Now she has just seven days to unravel the mystery of the Ring.', '/zHJFdhqEXZJxG653oZvOM3PmNON.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0298131', 'Rosenstrasse', 2003, 'Margarethe von Trotta', '/qH9KOehtacedYOMeO5zZMVzrDuC.jpg', null, 'When Ruth''s husband dies in New York, in 2000, she imposes strict Jewish mourning, which puzzles her children. A stranger comes to the house - Ruth''s cousin - with a picture of Ruth, age 8, in Berlin, with a woman the cousin says helped Ruth escape. Hannah, Ruth''s daughter engaged to a gentile, goes to Berlin to find the woman, Lena Fisher, now 90. Posing as a journalist investigating intermarriage, Hannah interviews Lena who tells the story of a week in 1943 when the Jewish husbands of Aryan women were detained in a building on Rosenstrasse. The women gather daily for word of their husbands. The film goes back and forth to tell Ruth and Lena''s story. How will it affect Hannah?', '/qbvsfbESs8napwhPDrEec8y2I6E.jpg', null, 0);
