INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0298203', '8 Mile', 2002, 'Curtis Hanson', '/v8mJvyNO86mcmY00FlxxRcOoDJy.jpg', null, 'The setting is Detroit in 1995. The city is divided by 8 Mile, a road that splits the town in half along racial lines. A young white rapper, Jimmy "B-Rabbit" Smith Jr. summons strength within himself to cross over these arbitrary boundaries to fulfill his dream of success in hip hop. With his pal Future and the three one third in place, all he has to do is not choke.', '/dXzTrKwpbLpCqn8O70FUUhNbYQT.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0298207', 'La Vida Rosa', 2001, 'Chito S. Roño', null, null, 'Crime does not pay, so the adage goes. But feisty and outspoken Rosa (Rosanna Roces)--however sharp-witted--does not know any better. Doggedly out for bigger money, she is quick to rebuff even her lover Dado''s (Diether Ocampo) pitch for a new life.  What will they risk to succeed? What will they give to taste the sweet pleasure of living peacefully and not running away from law and the syndicate anymore? Will their love for each other endure in the end?', '/zZNxFTCH5BQLVqWOIxbXWHt5iOY.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0298216', 'Blues Road Movie', 2002, 'Louis Mouchet', null, null, 'This film traces the road of the Blues and takes us on a journey to mythical places: From the banks of the Niger to New Orleans, going up the Mississippi through Memphis to the skyscrapers of Chicago.  It tells the story of this culture which faced the worst barriers and shows that Humanity can overcome barbarity.', '/1RoAphYksvDzagmJloZsaHODFcT.jpg', null, 0);
