INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0300186', 'My Brother the Vampire', 2001, 'Sven Taddicken', '/yv4XTpYXYhpvDkp7JGabyAs03Lu.jpg', null, 'Josh is about to turn 30; he''s mentally retarded, and likes to play at being a vampire. His sister Nicole is 15, and dealing with her impending womanhood; middle brother Mike is the "normal" one, but in some ways the most childish - his latest conquest is Nadine, who runs the dog kennel at his security guard job. Nic is fixated on getting laid, and watches Mike and Nadine through the skylight with Josh. Josh also gets fixated on sex, though he has no clear idea what it''s all about, and in particular on Nadine; to Mike''s chagrin, she seems more interested in Josh than in him.', '/sbRNyRvWIr8RqsKHm1ALpnbt3CN.jpg', null, 0);
