INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0304704', 'An Adolescent', 2001, 'Eiji Okuda', '/hhCUZEyLs2MrG0uu5gV3EtJe0h7.jpg', null, 'Tomokawa is a tough guy turned bored cop who spends much of his time sating lonely housewives and looking after retarded teen Sukemasa. One day while hanging out at a bar, he is approached by a 15-year-old enjo kosai named Yoko, offering a round of illicit sex in exchange for cash. Though he demurs, their paths cross again and soon a relationship of sorts forms. Yoko, it turns out, is Sukemasa''s sister; and both are the children of his old flame Yukie, a grasping, self-centered woman. Moreover, Yoko''s grandfather is responsible for the massive tattoo sprawling across Tomokawa''s back. Tomokawa soon takes both teens under his wing, protecting them from their heartless mother, and their lecherous stepfather. Soon, Yoko gets a similarly massive tattoo illustrating her bond with her policeman savior.', '/7sEwGLbSzeRY9x9l37qfpYTO0IU.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0304711', 'The Order', 2003, 'Brian Helgeland', '/rPSQSiTM5KiLxdlcoeTHjQ8FHKj.jpg', null, 'For centuries, a secret Order of priests has existed within the Church. A renegade priest, Father Alex Bernier, is sent to Rome to investigate the mysterious death of one of the Order''s most revered members. Following a series of strangely similar killings, Bernier launches an investigation that forces him to confront unimaginable evil.', '/rA4pObT4LkueerqbpIac5A7Ds3H.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0304722', 'Skinned Deep', 2004, 'Gabriel Bartalos', '/2AUTJFFIUNxmvVQI2msRuJBsd8m.jpg', null, 'The horror film Skinned Deep begins when a family suffers a flat tire on a barren stretch of road with only a diner dotting the landscape. They meet Granny, the seemingly nice old woman who runs the establishment, but they soon find that she is the leader of a deranged clan. The family is slain, with the exception of their teenage daughter, Tina whom one of the sons in the family, Brain (a boy with an externalized brain much larger than his head), takes a romantic interest in. Soon a group of bikers show up, forcing the girl to figure out which group of crazies she should throw in with in order to stay alive.', '/zH7mw2TpD0PFSHMoF9zf95zmAoE.jpg', null, 0);
