INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0310298', 'Vampire Clan', 2002, 'John Webb', '/cUjc2N3Y0vZhGOvON3UAhH8vawy.jpg', null, 'Five friends welcome real life vampirism into their lives. They cut their own wrists and let their friends drink from them. Rod, the leader, and a very psychotic young guy, ends up killing Heathers parents and stealing their car to go on a crazy raod trip. His behaviour begins to frighten his friends, though they went along with it at first. All end up in prison except Heather.', '/pavB2fIjISGWCjWXd4z1Z6f8xo5.jpg', null, 0);
