INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0323807', 'Remember Me, My Love', 2003, 'Gabriele Muccino', '/wqu0kdq7WAfKclvN43H6jgrhVPT.jpg', null, 'The story of a normal Italian family in which come out the dreams of those who have lost their possibilities and of those who want to realize them. Carlo and Giulia are a married couple who have each given up their aspirations in order to live an average life. Their 19-year-old son, Paolo, is having trouble finding an identity, while their 18-year-old daughter, Valentina, has already figured out how to use sex to her advantage. The family goes through a crisis when Carlo begins having an affair, Giulia attempt to seduce the director of a local stage production she is in, and Valentina does what she does best to land an audition for a TV show', '/abPnBHUm7b3r1NO2LGo0ltlBkRg.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0323808', 'The Wicker Tree', 2011, 'Robin Hardy', '/mfsJlzCDihws1MeoAllILAroQ46.jpg', null, 'Director Robin Hardy''s reimagining of his eerie 1973 film, The Wicker Man.  Young Christians Beth and Steve, a gospel singer and her cowboy boyfriend, leave Texas to preach door-to-door in Scotland. When, after initial abuse, they are welcomed with joy and elation to Tressock, the border fiefdom of Sir Lachlan Morrison, they assume their hosts simply want to hear more about Jesus. How innocent and wrong they are.', '/7ZsEaCfKkIm5DerhU3qx0TJdJMy.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0323836', 'Rosa la China', 2002, 'Valeria Sarmiento', null, null, 'Chilean director Valeria Sarmiento weaves a tale of love, betrayal, and civil unrest centering around a nightclub in 1950s pre-Communist Cuba in her 2002 romantic drama Rosa La China. Notorious businessman Santiago Ordenez (Juan Luis Galiardo), who primarily goes by the nickname Dulzara, operates a high-profile club/casino with the protection of some local politicians with whom he has dubious relationships. A large portion of the club''s success is due to its star singer, Rosa (Luisa Maria Jiminez), who also happens to be Dulzara''s lover. Rosa, however, has recently started into another affair with a somewhat younger womanizer named Marcos (Abel Rodriguez).', null, null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0323872', 'Kitchen Stories', 2003, 'Bent Hamer', '/apJii8MlmIbcREwMInFmNcF6pM2.jpg', null, 'Swedish efficiency researchers come to Norway for a study of Norwegian men, to optimize their use of their kitchen. Folke Nilsson (Tomas NorstrÃ¶m) is assigned to study the habits of Isak BjÃ¸rvik (Joachim Calmeyer). By the rules of the research institute, Folke has to sit on an umpire''s chair in Isak''s kitchen and observe him from there, but never talk to him. Isak stops using his kitchen and observes Folke through a hole in the ceiling instead. However, the two lonely men slowly overcome the initial post-war Norwegian-Swede distrust and become friends.', '/9Udh4Mxc3DI0UR5dxPTlrPkI0j9.jpg', null, 0);
