INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0323998', 'Slim Susie', 2003, 'Ulf Malmros', '/utcvjcob53tiujESrLHrYZYd5Lo.jpg', null, 'When Erik, a Stockholm urbanite, learns that his beauty-queen sister, Susie, is missing, he goes to their country roots to look for her. But after talking to the eccentric locals -- including a shy video store clerk and a corrupt police officer -- Erik finds a woman who is not at all like the girl he left behind. Award-winning director Ulf Malmros helms this black comedy infused with hipster flair.', '/zK55cwwdS3THvfSgUaZMQsExtq7.jpg', null, 0);
