INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0324067', 'Starbuck Holger Meins', 2002, 'Gerd Conradt', null, null, 'Twenty-five years after the death of Holger Meins, filmmaker and former student friend of the deceased, Gerd Conradt takes an in-depth look at the helmsman of the Baader-Meinhof gang. Who was Holger Meins? What led him into the underground? What circumstances resulted in his death, a death which made him the declared symbol of the radical opposition in Germany? What remains of his legacy?', '/xFAyHToszGVovV0Ybx3ACkKzMxu.jpg', null, 0);
