INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0324080', 'Stone Reader', 2002, 'Mark Moskowitz', null, null, 'Filmmaker Mark Moskowitz finally reads a critically acclaimed novel he bought back in 1972, and discovers that both the book and the author have long since vanished. STONE READER documents Moskowitz''s quest to find out why. As he solves the mystery, the twists and turns of the journey reveal something more-the singular bond literature can create among strangers.', '/fDKt9Wls42MCa7uYLXSdrdiB0bF.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0324112', 'Sundrapurushan', 2001, 'Jose Thomas', '/4ffQnFb3SrKjD6Jmp3LnumWuVi9.jpg', null, 'Musician &amp; odd-job man Soorya Narayanan (Suresh Gopi) is lured into playing the husband for mentally depressed Jyothika (Devayani), by Jyothika''s millionaire father Ramachandra Menon (N.F. Varghese) &amp; Jyothika''s doctor Geevarghese (Mukesh). They pull this off my plying Soorya''s greedy wife Sridevi (Nandini) with wealth &amp; riches, thus coaxing her to lend out her husband. However, Soorya gets in a bind when a rich Sridevi neglects him &amp; a married Jyothika shows improvement &amp; cares for him.', '/p5ronllIZBwVA99UaQE58nVE45E.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0324123', 'Surprise', 2002, 'Jin-seong Kim', null, null, 'How do you go about detaining a total stranger for 12 hours? This is what Ha-young (Lee Yo-won) has to do for her best friend, Mi-ryung (Kim Min-hee). Mi-ryung has been planning for a surprise party for her overseas boyfriend Jun-woo (Shin Ha-gyun), but her plans are stopped short when her father finds out that Jun-woo is half-Hawaiian, and forbids the party from being held. Mi-ryung is put under even greater pressure when Jun-Woo tells her that he would arrive early, and she pleads with Ha-Young to intercept Jun-Woo at the airport, and to keep him from turning up at Mi-ryung''s place before she can iron things out.', '/yVHIZa4fFOXBD7o2mkVEAs4746x.jpg', null, 0);
