INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0326450', 'The Star', 2002, 'Nikolay Lebedev', '/olHWg2biHwIt1IB5Pt5pGHJTNhU.jpg', null, 'The film is based on the eponymous book by Emmanuil Kazakevich. In the summer of 1944 the Nazi Armies prepare a massive Tank Division named ''Viking" for the offensive on occupied Russian land. The Russian Army''s special group of seven snipers named "Zvezda" is sent for a reconnaissance operation behind the enemy lines in the back of the Nazi Tank Division. Two previous Russian groups never came back. The seven Russians know that they are going to an almost certain Death for the sake of Victory.', '/aYgJi6tRBNpbToyfD7wTdauKTux.jpg', null, 0);
