INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0330713', 'Ocean Men: Extreme Dive', 2001, 'Bob Talbot', '/1v5rM8DWrhSWQWcpHEe4bmuwMy9.jpg', null, 'For more than 10 years, world champion freedivers Pipin Ferreras and Umberto Pelizzari have been vying for world records. Their love of the sea without compromise is what unites these two rivals. However, it is their different personalities and opposing diving philosophies that separate them. Pipin is the "No Limits" man who wants to go deeper and deeper. Umberto, the purist, seeks harmony in the ocean''s depth. Ocean Men takes you into the world of these two awe-inspiring freedivers through the use of breathtaking underwater photography, enchanting music, and insightful animation.', '/ugXdFKm7OoWLC4V2sUmcmbI9TvQ.jpg', null, 0);
