INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0332239', 'The Marsh', 2002, 'Kim Nguyen', null, null, 'Two social outcasts in 19th century Eastern Europe, Alexandre and Ulysse, become friends and settle down to live alone on the edge of a marsh that is reputedly haunted by demons, monsters and goblins. These two men have been outcasts all their lives. Alexandre was raised in a nomad family that traveled from town to town, being rejected by the people of his homeland. Due to his physical abnormalities, Ulysse has always been pushed aside ever since his childhood. When a strange murder is committed in a nearby village, the peasants turn their suspicions on Alexandre and Ulysse, because they are different. In the eyes of the villagers, they are demons that must be hunted, burned and killed.', '/lcs3gg8r9TYIIHRVI1i6KYirLe6.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0332260', 'Camel(s)', 2001, 'Ki-yong Park', null, null, 'One married man. One single woman. Both are in their 40â€™s. They sleep together on the day they first meet.  "Camel(s)" is a one-night story of a middle-aged couple who visited a small port village named ''Wol-got'' in the west coast. Unlike the classical love story, "Camel(s)" offers you a different taste of how a love affair can grow between two complete strangers in a black-and-white tone. The man (Lee Dae-yeon), as a chauffeur, brings the woman (Park Myung-shin) to ''Wol-got'', a used-to-be village but now a small town full of motels, sashimi and karaoke bars. Having no idea of who each other is, the two middle-aged people sit in the corner of a sashimi bar and start chatting. They have dinner together.  They sing karaoke together. They head for a motel together and finally they sleep together?', '/30NZT3tZnyOy2YzaeLjo34p9cpL.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0332280', 'The Notebook', 2004, 'Nick Cassavetes', '/AprhfmDEwy14xyjaAlcwdLolLjq.jpg', null, 'An epic love story centered around an older man who reads aloud to a woman with Alzheimer''s. From a faded notebook, the old man''s words bring to life the story about a couple who is separated by World War II, and is then passionately reunited, seven years later, after they have taken different paths.', '/gMfstesBXKdsHToAUXVPHujUDfb.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0332281', 'Happy End', 2003, 'Amos Kollek', '/cpwfTkploP7Cn00X8ZhvBtj1pe3.jpg', null, 'Val is 23 years old and full of dreams. She travels to New York to become an actress. She is lonely in a strange country, in a strange city, with little money and no friends. In her path, she meets weird people who they, also, seek their dreams but everyday life gets in the way. Tired and hungry she sits on the corner of a building. Across the street a writer whose fantasy has dry out. In an instant she becomes his muse... At the Oscar''s night she will be the one with the Golden Globe in her hands.', '/mWTJ5V3T2cdwXh5HJ0K1UBqR6vs.jpg', null, 0);
