INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0332318', 'Pinocchio 3000', 2004, 'Daniel Robichaud', '/uSJq7KwE2lb0rLugGLdruAdbOrX.jpg', null, 'This is the story of a little robot known as Pinocchio 3000 whose greatest wish is to become a real boy. The year is 3000. Geppetto, with the help of his faithful assistant, Spencer the cyber penguin, and by the holographic fairy Cyberina, creates Pinocchio, a prototype superrobot equipped for emotions. But before he can be given a heart and become a real boy, Cyberina insists that Pinocchio learn the difference between right and wrong.', '/bxhx00rqFhiUowjlvDiSpG6Ouvu.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0332322', 'Poniente', 2002, 'Chus Gutiérrez', '/6J6N01v6kaDcLWXZ3EhEjRn4sw6.jpg', null, 'After living in Madrid for many years as a teacher, Lucia returns to her hometown after her father''s death results in the inheritance of his tomato farm. While fighting the community''s racism toward the new illegal workers, Lucia falls in love with the farm''s accountant, Curro, a man who shares her cause. The couple is forced to confront the racism of the town and as tensions come to head, they will have to make some decisions that could cost them everything.', '/2shLfA0yqJJ9OLQqXFWiUNDXAEn.jpg', null, 0);
