INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0332639', 'Together', 2002, 'Kaige Chen', '/l6L9W1GdcJ5BbDqXentSoEovTkI.jpg', null, 'When a violin prodigy Xiaochun and his father head to Beijing seeking fame and fortune, they soon discover a fierce world of cutthroat ambition. But when Xiaochun is "adopted" by a famous music tutor, success finally seems within reach - until a shocking discovery begins to unravel his entire world, and the boy must make the most difficult choice of his life. Can he achieve the fame his father had always hoped for without losing the extraordinary passion that sets him apart?', '/z1tCZzOjn2qpRNxbPyNNv2iiXtz.jpg', null, 0);
