INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0339252', 'Khadgam', 2002, 'Krishna Vamshi', '/4eRs9Wsm0CwrO9uOYId5ONbXTGg.jpg', null, 'Khadgam  is a 2002 Telugu language Patriotic movie produced by Sunkara Madhu Murali under Karthikeya Movies banner. The film features Srikanth, Ravi Teja, Prakash Raj, Sangeetha, Sonali Bendre and Kim Sharma in the lead roles. Music has been composed Devi Sri Prasad. The movie released on 29 November 2002 was a hit at boxoffice[1]. Krishna Vamsi has directed the movie. Lyrics are penned by Sirivennela Sitaramasastri, Suddala Ashok Teja and Shakti. The movie was remade into Hindi by K.Subash with Ajay Devgan, Akshay Kumar and Tushar Kapoor and was titled Insan.', '/orEA0u5Aq9KWmq4ASthRlVSTEO3.jpg', null, 0);
