INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0349895', 'Nothing to Lose', 2002, 'Danny Pang', '/752YvGm6T4ORx6Xy2oRaV6XGhem.jpg', null, 'Thai crime-comedy-drama film written and directed by Danny Pang of the Pang Brothers. It is Danny Pang''s solo directorial debut, and is part of the loose "Bangkok Trilogy" by the Pangs that also includes Bangkok Dangerous (1999) and One Take Only (2003).  Somchai (Pierre Png), a debt-ridden gambling addict, goes to the top of a building to commit suicide and finds a young woman, Go-go (Arisara Wongchalee), standing on the ledge ready to do the same.  Rather than going through with the plans for death, the two talk and decide that there''s nothing they can''t do, since they had decided to die.  So they embark on a crime spree, starting out by eating in a restaurant and not paying the bill, then stealing a car and crashing it for fun.  They rob a convenience store, and are pursued by the police, and the gangsters Somchai pursue the couple as well.', '/kYZEKGlDzF9XPFZPZacodlYz8si.jpg', null, 0);
