INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0367195', 'Come and Go', 2003, 'João César Monteiro', '/cxX1lldlgLhuNTk7Bf4cRi1pSgk.jpg', null, 'Driven by his struggle against the illness that would ultimately prove fatal, Monteiro fervently devoted himself to his deeply personal last work, a musically structured meditation on spirituality and desire in which he heroically cast himself in the lead role as an eccentric libertine drifting wide-eyed through a sun-drenched Lisbon. A consciously terminal film, Come and Go offers a poetic summation of Monteiroâ€™s fascination with the body as the most irreverent and mysterious of temples, staging a series of playfully austere encounters between the visibly infirm and prematurely aged director and the women with whom he engages in erotic, linguistic and theological debates.', '/ajIGaSdcnOJGiVnPs2gYp0RHxNV.jpg', null, 0);
