INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0367942', 'Kutty', 2001, 'Janaki Vishwanathan', '/tSz8B6r9ANOWkJjlGEknUHC2HlX.jpg', null, 'The film revolves around a young girl who is forced into child labour working for an urban family after a tragedy in her own family. Paavaadai (Nassar) is a potter living in a village in the outskirts of Madurai. Even though his profession is in a miserable condition, he has high hopes for his daughter Kannammaa (Baby Swetha) and raises her with much affection. Unfortunately, he dies in an accident and this forces Kannammaa to be sent to work for an urban family.', '/cMkt041CsE0PpyOrZcrs7BHKuow.jpg', null, 0);
