INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0367960', 'The Legend of Johnny Lingo', 2003, 'Steven Ramirez', '/ouXYJmZfjPH82CLRu6yFRx33azB.jpg', null, 'When a storm washes a canoe bearing an infant boy ashore upon a small South Pacific island, he is at first well-received as a gift from the heavens, even to the point of the tribal chief adopting him as a successor. However, with time as hardships are blamed upon Tama (as he is named), he is finally outcast to live with the poorest people on the island, Mahana and her drunken father. Mahana is considered homely and undesirable, but Tama feels differently, so when he is old enough to build a craft to sail away, he vows to return for her one day. A lucky stroke brings him to land upon the island of the legendary Johnny Lingo, the wealthiest trader in the islands, and after years of service to him, Tama has learned much about life and himself as he dreams of honoring his pledge to Mahana.', '/b39D2EZD5nCU2EacnE6DZAuyGNv.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0367975', 'Lisbela and the Prisoner', 2003, 'Guel Arraes', '/4KDlC0cZvR5LtszqjFhZcMzdiUD.jpg', null, 'Lisbela is a young woman who loves going to the movies. LelÃ©u is a con man, going from town to town selling all sort of things and performing as master of ceremonies for some cheesy numbers, such as the woman who gets transformed into a gorilla. He gets involved with Linaura, a sexy and beautiful woman who happens to be the wife of the most frightening hitman of the place. The hitman finds out his wife''s affair and goes after LelÃ©u, who has to leave in a hurry. In another town, he meets and falls instantly in love with Lisbela, who is engaged to Douglas, a hillbilly who tries hard to pass for a cosmopolitan Rio de Janeiro dweller.', '/9nlaRxQeYaMrVVANMppagFohCRP.jpg', null, 0);
