INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0368559', 'In the Forest... Again', 2003, 'Goutam Ghose', null, null, 'The film features the characters from Satyajit Ray''s Aranyer Din Ratri, returning to the forest over thirty years later. Ashim, Sanjoy, Harinath and Aparna have grown old in this film; Shekhar has died. They set out on a journey to break off every link with civilisation for a few days. However, the trip turns sour when Ashim and Aparna''s daughter, Amrita, goes missing. It transpires that she is being held for ransom by local tribespeople. Police intervene and the kidnapped girl is returned to her parents, albeit against her own wishes.', '/246ro3sWSonXnhMT4GOUtlUqxuz.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0368563', 'Possession', 2008, 'Simon Sandquist', '/uIjVsKkVgrjVBaS6MJvkFdeKnKF.jpg', null, 'A woman''s life is thrown into chaos after a freak car accident sends her husband and brother-in-law into comas. Thrills arrive after the brother-in-law wakes up, thinking he''s his brother.', '/6E8M82rrOeWXZzWcXiPp7B04N9k.jpg', null, 0);
