INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0368571', 'Americano', 2005, 'Kevin Noland', '/m95hTd2NMexlWUudRPnn8x99qrt.jpg', null, '"Americano" centres around Chris McKinley (Jackson), a recent college graduate backpacking through Europe who savours his last three days of freedom before boarding the career fast track back in the United States. In Pamplona with two friends (Timm Sharp and Ruthanna Hopper), Chris meets an Australian thrill-seeker (Phil Barantini), a quintessential Spanish beauty (Varela) and an enigmatic provocateur (Dennis Hopper), all of whom encourage him to rethink his life. As the minutes and seconds until his departure tick away, Chris struggles with an age-old question: Should he follow the beaten path or risk it all on the road less travelled?', '/abz9OySFhmrN9HgyFMOcQTJRqPX.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0368578', 'Are We There Yet?', 2005, 'Brian Levant', '/yF74RJ1r87tqXbcPpeYs9AEXYbm.jpg', null, 'The fledgling romance between Nick, a playboy bachelor, and Suzanne, a divorced mother of two, is threatened by a particularly harrowing New Years Eve. When Suzanne''s work keeps her in Vancouver for the holiday, Nick offers to bring her kids to the city from Portland, Oregon. The kids, who have never liked any of the men their mom dates, are determined to turn the trip into a nightmare for Nick.', '/e081B8b8ZCAfeyof5Zm6dNwJOmp.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0368580', 'Asambhav', 2004, 'Rajiv Rai', '/8un42szwjklU17NRqLUIUHUrkHF.jpg', null, 'When the President of India is kidnapped by Kashmiri terrorists in Locarno, Switzerland, an Indian secret agent is sent to find him and set him free. With the help of an Indian singer he will make the "Impossible" to complete the mission.', '/c0G87YigTYW1O5bhNzxSNZ41AVQ.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0368595', 'Bedwin Hacker', 2003, 'Nadia El Fani', null, null, 'In the contemporary Tunisian thriller Bedwin Hacker, a female media pirate - holed up in a remote outpost in the mountains of Tunisia - figures out a way to take over the television and radio airwaves in France and North Africa to broadcast political messages. The irritated French Intelligence service closes in on her, which leads to a tit-for-tat game of intrigue between the hacker and her pursuers. Helmed by a female director, this film is of some historical significance, given its unusually frank, straightforward, and accurate portrayal of the day-to-day experiences of north African women.', null, null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0368619', 'Brides', 2004, 'Pantelis Voulgaris', '/gyAwYpJnA1k39H6wkI6WKwE4DTe.jpg', null, 'A young female character, Nikki Douka, from Samothraki, is sailing to the U.S. to fulfill a marriage contract and save her family honor. Her skills as a seamstress keep her busily sewing throughout the voyage to alter wedding dresses for the third-class voyagers. But along the way, she meets a young American photographer who is returning from the Middle East where he was snapping shots of the war in Smyrna, 1922. Her honesty, pride, and beauty attract the attention of the American who falls in love with her. Tribulations abound during the voyage, following the dramas of several unfortunate young women upon whom nasty characters preyed, as Nikki struggles with her feelings for the photographer.', '/v5d7Phti5dAZERhs3XP04ncoteA.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0368652', 'Claire', 2001, 'Milford Thomas', null, null, 'Loosely based on a Japanese fairytale, an elderly couple on a farm in the 1920''s South find a little girl from the moon inside an ear of corn and raise her as their own.', '/rtsjklUK6Bp1KLsmA9OjEyVU6Ip.jpg', null, 0);
