INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0378947', 'Melinda and Melinda', 2004, 'Woody Allen', '/voDE6XB0qaBK55MZBbIaTjaevIy.jpg', null, 'While dining out with friends, Sy suggests the difficulty of separating comedy from tragedy. To illustrate his point, he tells his guests two parallel stories about Melinda ; both versions have the same basic elements, but one take on her state of affairs leans toward levity, while the other is full of anguish. Each story involves Melinda coping with a recent divorce through substance abuse while beginning a romantic relationship with a close friend''s husband.', '/1gPOUQJMTWZpPnwd7lRkqmAR8wk.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0378957', 'Vajram', 2004, 'Pramod', '/7EOsw6TSLQxd3XO4hvmxyzGPfTQ.jpg', null, 'Vajram movie is about Devarajan (Mammootty), a Mafioso turned crane operator. Circumstances and the shenanigans of one of his relatives (Kalasala Babu) see to that Devarajan is separated from his rich wife Nanda (Nandini). In the event, he is also separated from his son Appu (Mithun).Amidst all this, one of the many villains in the film aims for the fortune and money of Nandini. Then there is Williams (Babu Antony) the scheming husband of actress Gemini (Vasundhra Das). He wants to eliminate her to inherit her wealth.', '/388t8puq8r4p6cXLdjKfsCWKVJ1.jpg', null, 0);
