INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0379215', 'Christmas', 2003, 'Gregory King', null, null, 'Broke, jobless and chronically depressed, oldest son Keri returns to the family home for Christmas, where he must face the emotional train-wreck that is his family: his exhausted parents, two messed-up sisters and a gay younger brother. A warts-and-all black comedic look at the horrors of suburbia, the awfulness of the holiday season and the ties that bind.', '/zkd4s0qgT1iLevcpzx8YzA9yvnV.jpg', null, 0);
