INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0409694', 'Anandam', 2001, 'Sreenu Vaitla', '/bi0nueLW4WTCo1g0VkvPWocdFxX.jpg', null, 'Kiran (Akash) and Aishwarya (Rekha) are neighbors for 20 years in Hyderabad. They study in the same college and immensely hate each other. Rekha''s parents shift to Ooty. In Ooty house, Aishwarya founds an unopened greeting card addressed to Deepika (Tanu Roy). Aishwarya comes to know that Deepika is the girl who stayed in that room in the past. Deepika committed suicide because her lover Vamsi (Venkat) did not return after going to Hyderabad. Aishwarya opens the letter addressed to Deepika and realizes that Vamsi wrote it. It''s a sensitive letter written by Vamsi explaining his feelings towards her. Aishwarya does not want to disappoint Vamsi that Deepika is no more. She starts writing letters on the name of ''Deepika''. Interestingly, on the other side Kiran would be replying all these mails on the name of ''Vamsi'', as Vamsi dies of accident in Hyderabad. The rest of the film is about how these two people come to know about the real story and turn the their hatred into love', '/856DE2pfuShKAJkl0ryROpfVS6B.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0409696', 'Someone Special', 2004, 'Jin Jang', '/A5qWxhUbEBj2YiSqTy43mjPbnaf.jpg', null, 'Director Jang Jin''s first romantic comedy presents a rare love story between a once famous baseball player and his ardent lover. Dong Chiseong (played by Jeong Jaeyeong), a star baseball pitcher in high school and college, hurts his shoulder, and is replaced as an outfielder. What is worse, he experiences a constant nose-bleeding and hears from the doctor that he has only three months to live due to a malignant tumor in his lung. Deeply depressed, he goes to a bar to drink away his sorrows until he falls asleep. A pretty bartender Han Iyeon (Lee Nayeong), finds him passed out and picks him up and carries him home.', '/yIPicyqv9CRu1qaDoS5GZmLOKXI.jpg', null, 0);
