INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0411674', 'Mondovino', 2004, 'Jonathan Nossiter', '/jQpWehuTftf2ZI6m9fIMulIU0U1.jpg', null, 'Mondovino (in Italian: World of Wine) is a 2004 documentary film on the impact of globalization on the world''s different wine regions written and directed by American film maker Jonathan Nossiter. It was nominated for the Palme d''Or at the 2004 Cannes Film Festival and a CÃ©sar Award.The film explores the impact of globalization on the various wine-producing regions, and the influence of critics like Robert Parker and consultants like Michel Rolland in defining an international style. It pits the ambitions of large, multinational wine producers, in particular Robert Mondavi, against the small, single estate wineries who have traditionally boasted wines with individual character driven by their terroir.', '/aVoGJN2eAOpJIXldMEJcQwOuutn.jpg', null, 0);
