INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0416120', 'Police Force: An Inside Story', 2004, 'Dilip Shukla', '/fS3shbEYroqx2RQfA5wQTj4L6F4.jpg', null, 'Senior police officer Pandey is called in to train a new batch of trainee officers that the Indian police force wants to recruit. He does manages to induct some sense of fair-play in these officers and gets them assign to their place of duty; but these officers will find that there real life scenario are different and must deal with the world at large consisting of corrupt high-ranking police officers, dishonest politicians and gangsters. How they deal with them will shape their destinies.', '/9FYNxCntzbbdeDBA2YjerKnUbHK.jpg', null, 0);
