INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0430984', 'Dark Heart', 2006, 'Kevin Lewis', '/unDxj4emMbtFaO6kg1X5WusXQeF.jpg', null, 'Upon returning home as a hero from the Iraq War, Special Forces Agent Matt Taylor (Joelson) is thrown right into another battle. This time it is in his once quiet hometown where, as in Iraq, you don''t know who are friends and who are enemies. In a dark night filled with mental and physical violence, Matt must again reach into the darkest corners of his mind to fight his inner demons and survive in the homeland he fought to defend. The Dream is over...', '/mDLHLw7olkYJj8PsRzVPKG6GD18.jpg', null, 0);
