INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0439182', 'Four Eyed Monsters', 2005, 'Susan Buice', '/ePuUq2fm9kIzs7e0dfnn1RV29Ku.jpg', null, 'Apathy, technology, paranoia, disease and medication. Meet Arin. Arin is a shy videographer who finds it too much to handle to go out and meet girls, so he sets up an account on meester.net. The flood of responses never comes, save for one email from Susan, a struggling artist who finds her job as a waitress stifling her creativity. Susan is also on the shy side and is seeking an alternative to the classic dating situation. When Arin and Susan finally meet, that alternative dating situation comes to life as the two refuse to communicate verbally with each other, wanting to avoid bs small talk.', '/sW6w6fuSkpAIB4rYiKWXEiAs8xL.jpg', null, 0);
