INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0439197', 'Heidi', 2005, 'Paul Marcus', '/jJu3OByvjSVqcn156hFRF9af20U.jpg', null, 'Swiss girl Adelheid ''Heidi'' is orphaned young. Aunt Detie brings her to grandpa Alp and his wife, who live isolated in the Alps since his murder charge. Heidi soon takes to the wild country, especially accompanying young goatherd Peter. Grandpa refuses to send her to school in the city, but aunt Detie returns and forces him to give in. She''s sent to a posh lady in Frankfurt, where she''ll be a companion for crippled daughter Clara after school hours.', '/p2VZt4f1u2qsn2I62I4arTdlgQk.jpg', null, 0);
