INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0439280', 'Private Moments', 2005, 'Jag Mundhra', '/5YD7IJz36C7djGgVpWHbbGQklzo.jpg', null, 'Serena Scott is a late-night host on London Talk Radio. Ratings are down, and she has only two weeks to turn things around or management will switch to elevator music. It''s also her 27th birthday, and three friends invite for a girls night in where they play "Truth or Dare." All but one chooses "Truth," and by the end of the evening, each woman''s story may give Serena an idea that will save her show, her job, and her private life.', '/cLDBJYTg8m0Xz6DF7U2vAUfmS7T.jpg', null, 0);
